graphql.query:
query {
  orders(query: {shipCity:"London"}) {
    _id
    freight
    orderDate
    requiredDate
    shipAddress
    shipCity
    shipCountry
    shipName
    shipPostalCode
    shipRegion
    shipVia
    shippedDate
    customer {
        customerId
        companyName
        contactName
        contactTitle
    }
    employee {
        employeeId
        firstName
        lastName
    }    
    orderDetails {
        discount
        quantity
        unitPrice
        product {
            productId
            productName
            quantityPerUnit
      }
    }
  }
}

